import { useState } from "react";
import { Header } from "./components/Header";
import { FoodList } from "./components/FoodList";
import { AddNewItem } from "./components/AddNewItem";

// List for food items
export const foodItems = [
  {
    image:
      "data:image/jpeg;base64,",
    name: "Burger",
    price: 10,
  },
  {
    image:
      "data:image/jpeg;base64,",
    name: "Vada Pav",
    price: 5,
  },
];

export default function App() {
  //   /* State for checking if the food list is open or not */
  const [isOrderListOpen, setIsOrderListOpen] = useState(false);
  const [isAddingNewItem, setIsAddingNewItem] = useState(false);
  const [foodItemList, setFoodItemList] = useState(foodItems);

  return (
    <div className="container">
      <Header
        isOrderListOpen={isOrderListOpen}
        setIsOrderListOpen={setIsOrderListOpen}
        setIsAddingNewItem={setIsAddingNewItem}
      />
      {isOrderListOpen && (
        <FoodList
          setIsOrderListOpen={setIsOrderListOpen}
          isAddingNewItem={isAddingNewItem}
          setIsAddingNewItem={setIsAddingNewItem}
          foodItemList={foodItemList}
        />
      )}
      {isAddingNewItem && (
        <AddNewItem
          setIsAddingNewItem={setIsAddingNewItem}
          foodItemList={foodItemList}
          setFoodItemList={setFoodItemList}
        />
      )}
    </div>
  );
}
